import React, { useState } from 'react';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('Customer');

  // 處理 email 改變事件
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  // 處理密碼改變事件
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  // 驗證 email 是否有效
  const isValidEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email); // 基本 email 格式驗證
  };

  // 驗證密碼是否符合要求
  const isValidPassword = (password) => {
    return /^(?=.*[a-zA-Z])(?=.*[0-9])(?=.*[!@#$%^&*]).{6,}$/.test(password); // 包含大小寫字母、數字和特殊符號，最少6個字元
  };

  // 處理註冊按鈕
  const handleRegister = () => {
    if (!isValidEmail(email)) {
      alert('請輸入有效的 Email 地址！');
      return;
    }
    if (!isValidPassword(password)) {
      alert('密碼必須包含字母、數字和特殊符號，且至少6個字元！');
      return;
    }
    alert(`註冊成功！帳號類型：${userType}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>登入介面</h1>

        <div className="form-container">
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={handleEmailChange}
              placeholder="請輸入 Email"
            />
          </label>
          <label>
            密碼:
            <input
              type="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder="請輸入密碼"
            />
          </label>

          <div className="button-group">
            <button onClick={() => setUserType('Customer')}>
              切換到 Customer
            </button>
            <button onClick={() => setUserType('Staff')}>
              切換到 Staff
            </button>
          </div>

          <p>目前使用者類型: {userType}</p>

          <button onClick={handleRegister} className="register-button">
            註冊
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
