import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StaffPage.css'; // ÂºïÂÖ•Ëá™Ë®ÇÊ®£Âºè


function StaffPage() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);  // ÊéßÂà∂ÂØÜÁ¢ºÈ°ØÁ§∫
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    staffId: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="staffpage-container">
      <h1 className="staffpage-title">Staff Login</h1>
      <form className="staffpage-form">
        <label className="staffpage-label">
          Email
          <input
             type="email"
             name="email"
             placeholder="Please Enter your Email"
             className="staffpage-input"
             value={formData.email}
             onChange={handleChange}
          />
        </label>
        <label className="staffpage-label">
          Password
          <div className="password-container">
            <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                placeholder="Please Enter your Password"
                className="staffpage-input"
                value={formData.password}
                onChange={handleChange}
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => setShowPassword(!showPassword)}
            >
              üëÅÔ∏è
            </button>
          </div>
        </label>
        <label className="staffpage-label">
          Staff ID
          <input
            type="text"
            name="staffId"
            placeholder="Please Enter your Staff ID"
            className="staffpage-input"
            value={formData.staffId}
            onChange={handleChange}
          />
        </label>

        {/* Login ÊåâÈàï */}
        <button type="button" className="staffpage-button-login">
          Login
        </button>

        {/* Return HomePage Âíå Register ÊåâÈàï‰∏¶ÊéíÈ°ØÁ§∫ */}
        <div className="staffpage-button-other">
          <button
            type="button"
            className="staffpage-button"
            onClick={() => navigate('/')}
          >
            Return HomePage
          </button>
          <button 
            type="button" 
            className="staffpage-button"
            onClick={() => navigate('/staffregister')}
            >
            Register
          </button>
          {/* Inventory ÊåâÈàï ÂæåÁ∫åÊúÉÊîπÂà∞ÁôªÂÖ•ÂæåÊâçËÉΩÊü•Ë©¢*/}
          <button
            type="button"
            className="staffpage-button"
            onClick={() => navigate('/inventory')}
          >
            Inventory
          </button>
        </div>
      </form>
    </div>
  );
}

export default StaffPage;
