import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StaffRegisterPage.css'; // 引入樣式

function StaffRegisterPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = () => {
    // 確認密碼是否匹配
    if (formData.password !== formData.confirmPassword) {
      alert('Passwords do not match!');
      return;
    }

    // 呼叫 API 儲存資料
    fetch('https://your-api-endpoint.com/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'your-authorization-token', // 替換為你的 Token
      },
      body: JSON.stringify({
        email: formData.email,
        password: formData.password,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Registration successful:', data);
        alert('Registration successful');
        setTimeout(() => navigate('/staffpage'), 10000); // 停留10秒後跳轉
      })
      .catch((error) => {
        console.error('Error during registration:', error);
        alert('Registration failed. Please try again.');
      });
  };

  return (
    <div className="registerpage-container">
      <h1 className="registerpage-title">Staff Register</h1>
      <form className="registerpage-form">
        <label className="registerpage-label">
          Email
          <input
            type="email"
            name="email"
            placeholder="Please Enter your Email"
            className="registerpage-input"
            value={formData.email}
            onChange={handleChange}
          />
        </label>

        <label className="registerpage-label">
          Password
          <div className="password-container">
            <input
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="Please Enter your Password"
              className="registerpage-input"
              value={formData.password}
              onChange={handleChange}
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => setShowPassword(!showPassword)}
            >
              👁️
            </button>
          </div>
        </label>

        <label className="registerpage-label">
          Confirm Password
          <div className="password-container">
            <input
              type={showConfirmPassword ? 'text' : 'password'}
              name="confirmPassword"
              placeholder="Please Confirm your Password"
              className="registerpage-input"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              👁️
            </button>
          </div>
        </label>

        {/* 顯示錯誤訊息 */}
        {formData.password !== formData.confirmPassword && (
          <div className="error-message">Passwords do not match!</div>
        )}

        <div className="registerpage-buttons">
          <button
            type="button"
            className="registerpage-button"
            onClick={() => navigate('/staff')}
          >
            Return
          </button>
          <button type="button" className="registerpage-button" onClick={handleSubmit}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default StaffRegisterPage;
