import React, { useState } from "react";
import "./InventoryTable.css"; // 引入樣式檔案

function InventoryTable() {
  const [items, setItems] = useState([
    { id: 1, name: "Product A", stock: 10, price: 100 },
    { id: 2, name: "Product B", stock: 5, price: 200 },
    { id: 3, name: "Product C", stock: 20, price: 300 },
  ]);

  const [editRowId, setEditRowId] = useState(null); // 控制編輯行
  const [searchName, setSearchName] = useState(''); // 搜尋名稱
  const [searchId, setSearchId] = useState(''); // 搜尋ID

  const handleEdit = (id) => {
    setEditRowId(id); // 設置正在編輯的行
  };

  const handleDelete = (id) => {
    setInventory((prevInventory) =>
      prevInventory.map((item) =>
        item.id === id ? { ...item, deleted: true } : item
      )
    );
  };

  const handleAdd = () => {
    const newId = inventory.length ? inventory[inventory.length - 1].id + 1 : 1;
    const newItem = { id: newId, name: '', quantity: '', price: '', deleted: false };
    setInventory([...inventory, newItem]);
    setEditRowId(newId); // 自動進入編輯模式
  };

  const handleSave = (id, field, value) => {
    setInventory((prevInventory) =>
      prevInventory.map((item) =>
        item.id === id ? { ...item, [field]: value } : item
      )
    );
  };

  const handleSearch = () => {
    // 搜尋後高亮
    const filtered = inventory.map((item) => {
      if (
        (!searchName || item.name.toLowerCase().includes(searchName.toLowerCase())) &&
        (!searchId || String(item.id).includes(searchId))
      ) {
        return { ...item, highlight: true };
      } else {
        return { ...item, highlight: false };
      }
    });
    setInventory(filtered);
  };

  return (
    <div className="inventory-container">
      <h1 className="inventory-title">Inventory Management</h1>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Item Number</th>
            <th>Item Name</th>
            <th>Inventory Quantity</th>
            <th>Price</th>
            <th>Increase/Decrease</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.stock}</td>
              <td>${item.price}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  className="inventory-input"
                  placeholder="Input Quantity"
                  onChange={(e) => {
                    const value = parseInt(e.target.value) || 0;
                    e.target.dataset.amount = value; // 暫存輸入數值
                  }}
                />
                <button
                  className="inventory-button add-button"
                  onClick={(e) =>
                    handle(item.id, parseInt(e.target.previousSibling.dataset.amount) || 0)
                  }
                >
                  Adjust
                </button>
                <button
                  className="inventory-button subtract-button"
                  onClick={(e) =>
                    handleSubtract(item.id, parseInt(e.target.previousSibling.dataset.amount) || 0)
                  }
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default InventoryTable;
